((__)=>{
    const KOOK='__j$$e$$ion__';
    class _ {
        constructor(){
          this.db=[];
        }
        register(id){
          this.db.push(new __(id));
          return this.db[this.db.length-1];
        }
        sessionsId(){
          return this.db.map(s=>s.id);
        }
        joinByCreation(rq,rs){
          let prv=this.find(rq);
          let curr=this.create(rq,rs);
          curr.previous=prv;
          return curr;
        }
        joinExisted(rq,rs){
            let s=this.find(rq);
            try {
              s.addHistroy(rq);
              return s;
            } catch (e) {

              return this.joinByCreation(rq,rs);
            }

        }
        joinCreateIfNotExist(rq,rs){
            if(!rq.cookies()[KOOK]){
              return this.joinByCreation(...arguments);
            }else{

                return this.joinExisted(rq,rs);
          }
        }
        join(rq,rs,ovrd){
            if(ovrd===false){
              return this.joinExisted(...arguments);
            }
            if(ovrd===true){
              return this.joinByCreation(...arguments);
            }
            return this.joinCreateIfNotExist(...arguments);
        }
        create(rq,rs,id){
          id= id || this.makeId();
          let s= this.register(id);
          rs.setCookie(KOOK,s.getId());
          s.addHistroy(rq);
          return s;
        }
        findById(id){
          if(id){
            let all=this.db.filter(s=>s.getId()===id);
            return all.length && all[0];
          }
        }
        find(rq){
            return this.findById(rq.cookies()[KOOK]);
        }

        generateString(size){
           var text = "",possible = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";
           for( var i=0; i < size; i++ )
               text += possible.charAt(Math.floor(Math.random() * possible.length));
           return text;
        }
        makeId(){
               let id=this.generateString(20)+this.now();
               while(this.sessionsId().indexOf(id)>=0){
                 id=this.generateString(20)+this.now();
               }
               return id;
        }
        now(){
          return new Date().getTime();
        }
    }


    module.exports=new _();
})(require('./Hsess.js'));
